import React, { useState, useEffect } from 'react';

function Sidebar() {

    const [isOpen, setIsOpen] = useState(true);
    const [colSize, setColSize] = useState("col-2");


    // Toggles the sidebar open or closed.
    const toggleSidebar = () => {
        try {
            if (isOpen == true){
                setIsOpen(false);
                setColSize("col-xs-0")
            } else {
                setIsOpen(true);
                setColSize("col-2")
            };        
        } catch (err) {
            console.error('Error toggling the sidebar:', err);
        }
    };

    return(
            
        <div id="sidebar" class={colSize}>



            <button className="btn btn-primary" onClick={toggleSidebar}>{isOpen.toString()}</button>
            <div class="container">
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
            </div>
        
        </div>
    );

};

export default Sidebar;
