import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Task from './components/Task';



function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  // Fetch tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  // Fetch all tasks from the back-end API
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:3000/tasks');
      setTasks(response.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // Handle form input for new tasks
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Create a new task
  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:3000/tasks', newTask);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '' });
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  // Delete a task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  // Update a task
  const updateTask = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/tasks/${currentTask.id}`, currentTask);
      setTasks(tasks.map((task) => (task.id === currentTask.id ? response.data : task)));
      setIsEditing(false);
      setCurrentTask(null);
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  // Set current task for editing
  const handleEdit = (task) => {
    setCurrentTask(task);
    setIsEditing(true);
  };

  // Handle form input for editing tasks
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>

      {/* New Task Form */}
      {!isEditing ? (
        <div>
          <h2>Add New Task</h2>
          <input
            type="text"
            name="title"
            placeholder="Task title"
            value={newTask.title}
            onChange={handleInputChange}
          />
          <textarea
            name="description"
            placeholder="Task description"
            value={newTask.description}
            onChange={handleInputChange}
          />
          <button onClick={addTask}>Add Task</button>
        </div>
      ) : (
        <div>
          <h2>Edit Task</h2>
          <input
            type="text"
            name="title"
            placeholder="Task title"
            value={currentTask.title}
            onChange={handleEditChange}
          />
          <textarea
            name="description"
            placeholder="Task description"
            value={currentTask.description}
            onChange={handleEditChange}
          />
          <button onClick={updateTask}>Update Task</button>
        </div>
      )}

      {/* Display Tasks */}
      <div>
        <h2>Tasks</h2>
        {tasks.map((task) => (
          <Task key={task.id} task={task} onDelete={deleteTask} onUpdate={handleEdit} />
        ))}
      </div>
    </div>
  );
}

export default App;
