import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AppletViewAll() {
  
  // --+--+-----------------+--+--
  // --+--+-- Sidebar ------+--+--
  // --+--+-----------------+--+--
  const [isOpen, setIsOpen] = useState(true);
  const [sidebarClass, setSidebarClass] = useState("col-2");
  const [toggleText, setToggleText] = useState("<<");


  const toggleSidebar = () => {
    try {
        if (isOpen === true){
            setIsOpen(false);
            setSidebarClass("sidebar-hidden");
            setToggleText(">>");
        } else {
            setIsOpen(true);
            setSidebarClass("");
            setToggleText("<<");
        };
    } catch (err) {
        console.error('Error toggling the sidebar:', err);
    }
  };


  // --+--+-----------------+--+--
  // --+--+-- Main Window --+--+--
  // --+--+-----------------+--+--
  const [tasks, setTasks] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  // Fetch tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  // Fetch all tasks from the back-end API
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:3000/tasks');
      setTasks(response.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // Delete a task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  // Update a task
  const updateTask = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/tasks/${currentTask.id}`, currentTask);
      setTasks(tasks.map((task) => (task.id === currentTask.id ? response.data : task)));
      setIsEditing(false);
      setCurrentTask(null);
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  // Set current task for editing
  const handleEdit = (task) => {
    setCurrentTask(task);
    setIsEditing(true);
  };

  // Handle form input for editing tasks
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (



        <><div id="sidebar" className={sidebarClass}>

      <div id="sidebar-sort" className="container">
        <div className="row">
          <div className="col-4 text-white">
            <button className="btn btn-danger" onClick={''}>A</button>
          </div>
          <div className="col-4 text-white">
            <button className="btn btn-danger" onClick={''}>B</button>
          </div>
          <div className="col-4 text-white">
            <button className="btn btn-danger" onClick={''}>C</button>
          </div>
        </div>
      </div>

      <div id="sidebar-list" className="container">
        <div className="row text-white">Hello World</div>
        <div className="row text-white">Hello World</div>
        <div className="row text-white">Hello World</div>
        <div className="row text-white">Hello World</div>
      </div>

    </div><button id="sidebar-toggle" className="btn btn-primary" onClick={toggleSidebar}>{toggleText}</button><div id="applet-window" className="container-fluid">



        <div className="row h-100">

          {/* MAIN WINDOW */}
          <div id="applet-view-all" className="App container col">

            {/* Update Existing Task */}
            {isEditing === true ? (
              <div className="card mb-4 p-3">
                <h2 className="mb-3">Edit Task</h2>
                <input
                  type="text"
                  name="title"
                  placeholder="Task title"
                  className="form-control mb-3"
                  value={currentTask.title}
                  onChange={handleEditChange} />
                <textarea
                  name="description"
                  placeholder="Task description"
                  className="form-control mb-3"
                  value={currentTask.description}
                  onChange={handleEditChange} />
                <button className="btn btn-success" onClick={updateTask}>Update Task</button>
              </div>
            ) : (
              <></>
            )}

            {/* Display Tasks */}
            <div className="list-group">
              {tasks.map((task) => (
                <div className="list-group-item d-flex justify-content-between align-items-center" key={task.id}>
                  <div>
                    <h5>{task.title}</h5>
                    <p>{task.description}</p>
                  </div>
                  <div>
                    <button className="btn btn-danger" onClick={() => deleteTask(task.id)}>Delete</button>
                    <button className="btn btn-secondary ms-2" onClick={() => handleEdit(task)}>Edit</button>
                  </div>
                </div>
              ))}
            </div>

          </div>

        </div>

      </div></>

  );
}

export default AppletViewAll;
