import React, { useContext, useState } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletAddNewClient() {

	const { setTasks } = useContext(SharedStateContext);
	const { setAppletViewState } = useContext(SharedStateContext);
	const { setActiveClientID } = useContext(SharedStateContext);

	const [newTask, setNewTask] = useState({ title: '', description: '' });

	// Handle form input for new tasks
	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setNewTask((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	// Create a new task
	const addTask = async () => {
		try {
			const postNew = await axios.post('http://localhost:3000/tasks', newTask);
			const response = await axios.get('http://localhost:3000/tasks');
			setActiveClientID(postNew.data.id);				// Sets new client's ID as 'active'
			
			setAppletViewState("active-clients");			// Changes applet view to view the added client
			setNewTask({ title: '', description: '' });		// Resets 'new client' fields
			
			setTasks(response.data);						// Reloads list of clients
			

		} catch (err) {
			console.error('Error adding task:', err);
		}
	};

	return (

		/* --+-- ADD NEW CLIENT --+-- */
		<div id="applet-add-new" className="applet-view">

				{/* Header */}
				<h2 className="mb-3">Add New Client</h2>

				{/* Client Name */}
				<input
					type="text"
					name="title"
					placeholder="Company Name"
					className="form-control mb-3"
					value={newTask.title}
					onChange={handleInputChange}
				/>

				{/* Client Description */}
				<textarea
					name="description"
					placeholder="Company Description"
					className="form-control mb-3"
					value={newTask.description}
					onChange={handleInputChange}
				/>

				{/* Submit Button */}
				<button className="btn btn-primary" onClick={addTask}>Add Client</button>

		</div>
	);
};
