import React, { useState, useEffect } from 'react';

function Sidebar() {

    const [isOpen, setIsOpen] = useState(true);

    // Toggles the sidebar open or closed.
    const toggleSidebar = () => {
        try {
            if (isOpen == true){
                setIsOpen(false);
            } else {
                setIsOpen(true);
            };        
        } catch (err) {
            console.error('Error toggling the sidebar:', err);
        }
    };

    return(
            
        <div id="sidebar" class="col-2 bg-dark">



            <button className="btn btn-primary" onClick={toggleSidebar}>{isOpen}</button>
            <div class="container">
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
            </div>
        
        </div>
    );

};

export default Sidebar;
