import React, { useState } from 'react';

import Navbar from './Navbar';
import Footer from './Footer';

import AppletViewAll from './views/Applet-ViewAll';
import AppletAddNew from './views/Applet-AddNew';

// export const SharedStateContext = createContext();


const [appletViewState, setAppletViewState] = useState("home");



// export function SharedStateProvider({ children }) {

//   const [appletViewState, setAppletViewState] = useState("home");

//   return (
//     <SharedStateContext.Provider value={{ appletViewState, setAppletViewState }}>
//       {children}
//     </SharedStateContext.Provider>
//   );

// }


export function App() {  

  // const { appletViewState } = useContext(SharedStateContext);

  return (
    <>

      {appletViewState === "home" ? (
        <>
          <Navbar />
          <div id="applet-window">
            <div className="container-fluid h-100">
              <div className="row h-100">
                Hello World!!!
              </div>
            </div>
          </div>
          <Footer />
        </>
      ) : (
        <></>
      )}

      {appletViewState === "clients" ? (
          <SharedStateProvider>
          <Navbar />
          <div id="applet-window">
            <AppletViewAll />
          </div>
          <Footer />
        </SharedStateProvider>
      ) : (
        <></>
      )}

      {appletViewState === "new-client" ? (
          <SharedStateProvider>
          <Navbar />
          <div id="applet-window">
            <AppletAddNew />
          </div>
          <Footer />
        </SharedStateProvider>
      ) : (
        <></>
      )}

    </>
  );
}

export default App;
