import React, { useContext, useState, useEffect } from 'react';
import { ReactComponent as Icon_Sidebar } from "./../assets/layout-sidebar-inset.svg"

import axios from 'axios';

function Sidebar() {

	const [tasks, setTasks] = useState([]);
	const [isEditing, setIsEditing] = useState(false);
	const [currentTask, setCurrentTask] = useState(null);

	// Fetch tasks on component mount
	useEffect(() => {
		fetchTasks();
	}, []);

	// Fetch all tasks from the back-end API
	const fetchTasks = async () => {
		try {
			const response = await axios.get('http://localhost:3000/tasks');
			setTasks(response.data);
		} catch (err) {
			console.error('Error fetching tasks:', err);
		}
	};

	// Delete a task
	const deleteTask = async (id) => {
		try {
			await axios.delete(`http://localhost:3000/tasks/${id}`);
			setTasks(tasks.filter((task) => task.id !== id));
		} catch (err) {
			console.error('Error deleting task:', err);
		}
	};

	// Update a task
	const updateTask = async () => {
		try {
			const response = await axios.put(`http://localhost:3000/tasks/${currentTask.id}`, currentTask);
			setTasks(tasks.map((task) => (task.id === currentTask.id ? response.data : task)));
			setIsEditing(false);
			setCurrentTask(null);
		} catch (err) {
			console.error('Error updating task:', err);
		}
	};

	// Set current task for editing
	const handleEdit = (task) => {
		setCurrentTask(task);
		setIsEditing(true);
	};

	// Handle form input for editing tasks
	const handleEditChange = (e) => {
		const { name, value } = e.target;
		setCurrentTask((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	return (
		<>
			{/* SIDEBAR */}
			<nav id="sidebar" className="collapse collapse-horizontal">

				<div id="sidebar-wrapper" className="container">

					<div id="sidebar-sort" className="row">
						<button className="btn btn-danger btn-sort col">A</button>
						<button className="btn btn-danger btn-sort col">B</button>
						<button className="btn btn-danger btn-sort col">C</button>
					</div>

					<div id="sidebar-list" className="container">
						<div className="row text-white">Hello World</div>
						<div className="row text-white">Hello World</div>
						<div className="row text-white">Hello World</div>
						<div className="row text-white">Hello World</div>
					</div>

					{/* Display Tasks */}
					<div className="list-group">
						{tasks.map((task) => (
							<div className="list-group-item d-flex justify-content-between align-items-center" key={task.id}>
								<div>
									<h5>{task.title}</h5>
									<p>{task.description}</p>
								</div>
							</div>
						))}
					</div>

				</div>

			</nav>


			<button id="sidebar-toggle" type="button" data-bs-toggle="collapse" data-bs-target="#sidebar" aria-expanded="false" aria-controls="sidebar"><Icon_Sidebar /></button>

		</>

	);

};

export default Sidebar;
