import { useState } from "react";

var appletView = "home";

function Navbar() {

    const [ appletConst, setAppletConst ] = useState(appletView)

    const setView = (view) => {
        try {
            // appletView = view;
            setAppletConst(view);
        } catch (err) {
            console.error('Error setting applet view from navbar:', err);
        }
    }

    appletView = appletConst;

    return(
        
        /* NAVBAR */
        <nav id="navbar" className="navbar sticky-top navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">

                <div className="collapse navbar-collapse" id="navbarNav">
                
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <a className="nav-link" href="" onClick={setView("home")}>Home</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="" onClick={setView("clients")}>Clients</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="" onClick={setView("new-client")}>[Add New Client]</a>
                        </li>
                    </ul>

                </div>
                
            </div>
        </nav>
    );

};

export { appletView };
export default Navbar;
