import React, { useContext, useState } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletAddNewClient() {

	const { setClients, setAppletViewState, setActiveClientID } = useContext(SharedStateContext);

	const [newClient, setNewClient] = useState({
        name: '', website: '', description: '', descriptionaddon: '',
        products: {}, personas: {}, linkedin: '', youtube: '',
        twitter: '', facebook: '', instagram: '', tiktok: ''
    });

	// Handle form input for new clients
	const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewClient((prev) => ({ ...prev, [name]: value }));
    };

	// Create a new client
	const addClient = async () => {
        try {
            const postNew = await axios.post(`${process.env.REACT_APP_API_URL}/clients`, newClient);
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/clients`);
            setActiveClientID(postNew.data.id);
            setAppletViewState("active-clients");
            setClients(response.data);
            setNewClient({
                name: '', website: '', description: '', descriptionaddon: '',
                products: {}, personas: {}, linkedin: '', youtube: '',
                twitter: '', facebook: '', instagram: '', tiktok: ''
            });
        } catch (err) {
            console.error('Error adding client:', err);
            alert('Failed to add client. Please try again.');
        }
    };

	return (

		/* --+-- ADD NEW CLIENT --+-- */
		<div id="applet-add-new" className="applet-view">
            
			<h2 className="mb-3">Add New Client</h2>
            
			{['name', 'website', 'description', 'descriptionaddon', 'linkedin', 'youtube', 'twitter', 'facebook', 'instagram', 'tiktok'].map((field) => (
                <div key={field}>
                    <h6>{field.charAt(0).toUpperCase() + field.slice(1)}:</h6>
                    <input
                        type="text"
                        name={field}
                        placeholder={`Client ${field.charAt(0).toUpperCase() + field.slice(1)}`}
                        className="form-control mb-3"
                        value={newClient[field]}
                        onChange={handleInputChange}
                    />
                </div>
            ))}

            <button className="btn btn-primary" onClick={addClient}>Add Client</button>
        </div>
		
	);
};
