import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletViewAllClient() {

	const { tasks, setTasks } = useContext(SharedStateContext);

	// Fetch tasks on component mount
	useEffect(() => {
		fetchTasks();
	}, []);

	// Fetch all tasks from the back-end API
	const fetchTasks = async () => {
		try {
			const response = await axios.get('http://localhost:3000/tasks');
			setTasks(response.data);
		} catch (err) {
			console.error('Error fetching tasks:', err);
		}
	};

	return (

		/* --+--+-- VIEW ALL CLIENTS --+--+-- */
		<div id="applet-view-all" className="applet-view">

			{/* Display Clients */}
			<div className="list-group">
				{tasks.map((task) => (
					<div className="list-group-item d-flex justify-content-between align-items-center" key={task.id}>
						<div>
							<h5>{task.title}</h5>
							<p>{task.description}</p>
						</div>
					</div>
				))}
			</div>

		</div>
	);
};
