import React, { useState, useContext } from 'react';
import { SharedStateContext } from './_SharedStateComponent';

function Sidebar() {

    const [isOpen, setIsOpen] = useState(true);
    const [sidebarClass, setSidebarClass] = useState("col-2");
    const [toggleText, setToggleText] = useState("<<");

    const { appletViewState } = useContext(SharedStateContext);

    // Toggles the sidebar open or closed.
    const toggleSidebar = () => {
        try {
            if (isOpen === true){
                setIsOpen(false);
                setSidebarClass("sidebar-hidden");
                setToggleText(">>");
            } else {
                setIsOpen(true);
                setSidebarClass("");
                setToggleText("<<");
            };
        } catch (err) {
            console.error('Error toggling the sidebar:', err);
        }
    };

    return(
        
        <>
        {appletViewState === "home" ? (
            <></>
        ) : (
            <></>
        )}
        
        {appletViewState === "clients" ? (
            <>
            <div id="sidebar" className={sidebarClass}>

                <div id="sidebar-sort" className="container">
                    <div className="row">
                        <div className="col-4 text-white">
                            <button className="btn btn-danger" onClick={''}>A</button>
                        </div>
                        <div className="col-4 text-white">
                            <button className="btn btn-danger" onClick={''}>B</button>
                        </div>
                        <div className="col-4 text-white">
                            <button className="btn btn-danger" onClick={''}>C</button>
                        </div>
                    </div>
                </div>

                <div id="sidebar-list" className="container">
                    <div className="row text-white">Hello World</div>
                    <div className="row text-white">Hello World</div>
                    <div className="row text-white">Hello World</div>
                    <div className="row text-white">Hello World</div>
                </div>

            </div><button id="sidebar-toggle" className="btn btn-primary" onClick={toggleSidebar}>{toggleText}</button>
            </>
        ) : (
            <></>
        )}

        {appletViewState === "new-client" ? (
            <></>
        ) : (
            <></>
        )}
        </>

    );

};

export default Sidebar;
