import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';

import 'bootstrap/dist/css/bootstrap.min.css';

import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import Applet from './components/Applet';
import Footer from './components/Footer';

import './styles/App.css';

// SHARE STATES ACROSS COMPONENTS
export function ParentComponent() {

  const [appletViewState, setAppletViewState] = useState({value: "new-client"});

  return (
    <div>
      <Navbar appletViewState={appletViewState} setAppletViewState={setAppletViewState} />
      <Applet appletViewState={appletViewState} setAppletViewState={setAppletViewState} />
    </div>
  );

};


// RENDER THE DOM
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(

    <>

        <Navbar />

        {/* MAIN APPLET */}
        <div id="applet-window" className="container-fluid">
          <div className="row h-100">
            <Sidebar />
            <Applet />
          </div>
        </div>

        <Footer />

    </>

);
