import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletViewActiveClient() {

	const { clients, setClients } = useContext(SharedStateContext);
	const { activeClientID } = useContext(SharedStateContext);

	//------------------------------------------------------------------------------------
	//-- TODO: Update this so that the edit only updates an individual field -------------
	//------------------------------------------------------------------------------------

	// TODO: Update Sidebar when a client is updated.

	const [isEditing, setIsEditing] = useState(false);
	const [currentClient, setCurrentClient] = useState(null);

	// Update a client
	const updateClient = async () => {
		try {
			const response = await axios.put(`http://localhost:3000/clients/${currentClient.id}`, currentClient);
			setClients(clients.map((client) => (client.id === currentClient.id ? response.data : client)));
			setIsEditing(false);
			setCurrentClient(null);
		} catch (err) {
			console.error('Error updating client:', err);
		}
	};

	// Set current client for editing
	const handleEdit = (client) => {
		setCurrentClient(client);
		setIsEditing(true);
	};

	// Handle form input for editing clients
	const handleEditChange = (e) => {
		const { name, value } = e.target;
		setCurrentClient((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	// Delete a client
	const deleteClient = async (id) => {
		try {
			await axios.delete(`http://localhost:3000/clients/${id}`);
			setClients(clients.filter((client) => client.id !== id));
		} catch (err) {
			console.error('Error deleting client:', err);
		}
	};

	var clientEdits = <></>;

	if (isEditing === true) {
		clientEdits =
		<>

			{/* Header */}
			<h2 className="mb-3">Edit Client</h2>

			{/* Client Name */}
			<input
				type="text"
				name="name"
				placeholder="Client Name"
				className="form-control mb-3"
				value={currentClient.name}
				onChange={handleEditChange}
			/>

			{/* Client Description */}
			<textarea
				name="description"
				placeholder="Client Description"
				className="form-control mb-3"
				value={currentClient.description}
				onChange={handleEditChange}
			/>

			{/* Client Description Add-on */}
			<textarea
				name="descriptionaddon"
				placeholder="Additional Client Information"
				className="form-control mb-3"
				value={currentClient.descriptionaddon}
				onChange={handleEditChange}
			/>

			{/* Client Website */}
			<input
				type="text"
				name="website"
				placeholder="Client Website"
				className="form-control mb-3"
				value={currentClient.website}
				onChange={handleEditChange}
			/>

			{/* Client Social URLs */}
			<input
				type="text"
				name="social"
				placeholder="Client Social Media"
				className="form-control mb-3"
				value={currentClient.social}
				onChange={handleEditChange}
			/>

			{/* Client Products & Services */}
			<input
				type="text"
				name="products"
				placeholder="Client Products & Services"
				className="form-control mb-3"
				value={currentClient.products}
				onChange={handleEditChange}
			/>

			{/* Client Target Personas */}
			<textarea
				name="personas"
				placeholder="Client Target Persona"
				className="form-control mb-3"
				value={currentClient.personas}
				onChange={handleEditChange}
			/>

			{/* Submit Button */}
			<button className="btn btn-success" onClick={updateClient}>Update Client</button>

		</>
	} else {
		<></>
	};

	//------------------------------------------------------------------------------------
	//------------------------------------------------------------------------------------

	// Updates the active tabs and the sub-views
	function openTab(evt, tabName) {

		var i, tablinks, tabcontent;

		// Get all elements with class="tabcontent" and hide them
		tabcontent = document.getElementsByClassName("tabcontent");
		for (i = 0; i < tabcontent.length; i++) {
			tabcontent[i].style.display = "none";
			tabcontent[i].className = tabcontent[i].className.replace(" tabDefault", "");
		}

		// Get all elements with class="tablinks" and remove the class "active"
		tablinks = document.getElementsByClassName("tablinks");
		for (i = 0; i < tablinks.length; i++) {
			tablinks[i].className = tablinks[i].className.replace(" active", "");
		}

		// Show the current tab, and add an "active" class to the button that opened the tab
		document.getElementById(tabName).style.display = "block";
		evt.currentTarget.className += " active";

		setIsEditing(false);
	}


	const output = clients.map((client) => (client.id === activeClientID ? (
		<>
			{/* Mini Nav Bar */}
			<div class="container">
			<div className="tab">

				{/* Tabs for navigating client sub-views */}
				<div id="client-tabs" className="row">

					<div id="active-client-title">
						<h1 className="tabtitle">{client.name}</h1>
					</div>

					<div id="active-client-tabs" className="col">
						<button className="tablinks active" onClick={(event) => openTab(event, 'about')}>About</button>
						<button className="tablinks" onClick={(event) => openTab(event, 'personas')}>Target Personas</button>
						<button className="tablinks" onClick={(event) => openTab(event, 'topics')}>Topics</button>
						<button className="tablinks" onClick={(event) => openTab(event, 'settings')}>Settings</button>
					</div>

				</div>
			
			</div>
			</div>



			{/* Main window */}
			<div class="container">
			<div className="client-window">
				
				{/* Windowlet for client info */}
				<div class="row">
				<div id="about" className="tabcontent tabDefault">

					{/* Section 01 */}
					<div className="col">

						{/* Description */}
						<p>{client.description}</p>
						<p>{client.descriptionaddon}</p>

						{/* Website & Social Links */}
						<div id="link-group-wrapper">
							<h3>Links</h3>
							<div id='link-group'>
								{client.website
									? <a href={client.website}><div className='link-group-item'>Website</div></a>
									: <></>
								}
								{JSON.parse(client.products)
									? <>{JSON.parse(client.social).map((account) => (<a href={account.url}><div className='link-group-item'>{account.name}</div></a>))}</>
									: <></>
								}
								{!client.website && !JSON.parse(client.products)
									? <div className='link-group-item'>No Links Found</div>
									: <></>
								}
							</div>
						</div>

					</div>

					{/* Section 02 */}
					<div className="col">

						{/* Products & Services */}
						<div id="product-group-wrapper">
							<h3>Products & Services</h3>
							<div className='product-group'>
								{JSON.parse(client.products)
									?
									JSON.parse(client.products).map((product) => (
										<div className='product-group-item'>
											<h5>{product.name}</h5>
											<p>{product.description}</p>
										</div>
									))
									:
									<div className='product-group-item'>No Products & Services Found</div>
								}
							</div>
						</div>

					</div>

				</div>
				</div>



				{/* Window for Target Personas */}
				<div id="personas" className="tabcontent">
					<h3>Target Personas for {client.name}</h3>
				</div>



				{/* Window for Toppics */}
				<div id="topics" className="tabcontent">
					<h3>Content Topics for {client.name}</h3>
				</div>



				{/* Window for Settings */}
				<div id="settings" className="tabcontent">
					<h3>Update Client Info</h3>
					{clientEdits}
					{/* <button className="btn btn-danger" onClick={() => deleteClient(client.id)}>Delete</button> */}
					<button className="btn btn-danger" onClick={() => console.log("Delete Button Has Been Deactivated")}>Delete</button>
					<button className="btn btn-secondary" onClick={() => handleEdit(client)}>Edit</button>
				</div>

			</div>
			</div>
		</>
	) : (
		<></>
	)))

	return (

		/* --+--+-- VIEW ACTIVE CLIENT --+--+-- */
		<div id="applet-view-active" className="applet-view">

			{/* Display Client Panel */}
			{output}

		</div>

	);
};
