import React from 'react';

const Task = ({ task, onDelete, onUpdate }) => {
  return (
    <div>
      <h3 class="taskTitle">{task.title}</h3>
      <button class="taskButtonDelete" onClick={() => onDelete(task.id)}>Delete</button>
      <button class="taskButtonEdit" onClick={() => onUpdate(task)}>Edit</button>
      <p class="taskDescription">{task.description}</p>
    </div>
  );
};

export default Task;
