import React, { useContext, useEffect } from 'react';
import axios from 'axios';

import { SharedStateContext } from './_SharedStateComponent';

export default function AppletViewAllClient() {

	const { clients, setClients } = useContext(SharedStateContext);

	// Fetch clients on component mount
	useEffect(() => {
		fetchClients();
	}, []);

	// Fetch all clients from the back-end API
	const fetchClients = async () => {
		try {
			const response = await axios.get('http://localhost:3000/clients');
			setClients(response.data);
		} catch (err) {
			console.error('Error fetching clients:', err);
		}
	};

	const renderTabView = () => {

		return (
			
			/* View All Clients */
			<div id="tabcontent-allclients" className="view-window row tabcontent">


				{/* Section XX - Client Cards */}
				{clients.sort((a, b) => a.name.toLowerCase() > b.name.toLowerCase() ? 1 : -1 ).map((client, index) => (		
				<>
					
					{/* Client Card */}
					<div className='view-section col-12' key={index}>
						<div className='row' key={index}>

							{/* Client Card Header */}
							<div className="col-12" key={index}>
								<h1 key={index}>{client.name}</h1>
							</div>

							{/* Client Card Body */}
							<div id="description-wrapper" className="view-wrapper col-md-12 col-12">
								<h2>Description</h2>
								<p>{client.description}</p>
							</div>
						</div>
					</div>
				</>
				))}

			</div>

		)
	};

	return (

		/* --+--+-- VIEW ALL CLIENTS --+--+-- */
		<div id="applet" className="col">

			{/* Header Element for Section Title & Nav Elements */}
			<div className="view-header row">
				<div className="view-header title">All Clients</div>
			</div>

			{/* Body Element for Displaying Section Content */}
			<div className="view-body row">
				{renderTabView()}
			</div>

		</div>
	);
};
