import React, { useContext, useState } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletAddNewClient() {

	const { setClients } = useContext(SharedStateContext);
	const { setAppletViewState } = useContext(SharedStateContext);
	const { setActiveClientID } = useContext(SharedStateContext);

	const [newClient, setNewClient] = useState({ name: '', description: '' });

	// Handle form input for new clients
	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setNewClient((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	// Create a new client
	const addClient = async () => {
		try {
			const postNew = await axios.post('http://localhost:3000/clients', newClient);
			const response = await axios.get('http://localhost:3000/clients');
			setActiveClientID(postNew.data.id);				// Sets new client's ID as 'active'
			setAppletViewState("active-clients");			// Changes applet view to view the added client
			setNewClient({ name: '', description: '' });	// Resets 'new client' fields
			setClients(response.data);						// Reloads list of clients
		} catch (err) {
			console.error('Error adding client:', err);
		}
	};

	return (

		/* --+-- ADD NEW CLIENT --+-- */
		<div id="applet-add-new" className="applet-view">

				{/* Header */}
				<h2 className="mb-3">Add New Client</h2>

				{/* Client Name */}
				<input
					type="text"
					name="name"
					placeholder="Company Name"
					className="form-control mb-3"
					value={newClient.name}
					onChange={handleInputChange}
				/>

				{/* Client Description */}
				<textarea
					name="description"
					placeholder="Company Description"
					className="form-control mb-3"
					value={newClient.description}
					onChange={handleInputChange}
				/>

				{/* Submit Button */}
				<button className="btn btn-primary" onClick={addClient}>Add Client</button>

		</div>
	);
};
