import React, { useState, useEffect } from 'react';

function Sidebar() {

    const [isOpen, setIsOpen] = useState(true);
    const [sidebarClass, setSidebarClass] = useState("col-2");


    // Toggles the sidebar open or closed.
    const toggleSidebar = () => {
        try {
            if (isOpen == true){
                setIsOpen(false);
                setSidebarClass("col-1");
            } else {
                setIsOpen(true);
                setSidebarClass("col-2");
            };
        } catch (err) {
            console.error('Error toggling the sidebar:', err);
        }
    };

    return(
        
        <>
        
        <div id="sidebar" class={sidebarClass}>

            <div class="container">
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
            </div>

        </div>

        <button id="sidebar-toggle" className="btn btn-primary" onClick={toggleSidebar}>{isOpen.toString()}</button>
        
        </>

    );

};

export default Sidebar;
