import React, { useContext, useState } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletAddNewClient() {

	const { setAppletViewState } = useContext(SharedStateContext);

	const [newTask, setNewTask] = useState({ title: '', description: '' });

	// Handle form input for new tasks
	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setNewTask((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	// Create a new task
	const addTask = async () => {
		try {
			await axios.post('http://localhost:3000/tasks', newTask);
			setNewTask({ title: '', description: '' });
			setAppletViewState("clients");
		} catch (err) {
			console.error('Error adding task:', err);
		}
	};

	return (

		/* --+-- ADD NEW CLIENT --+-- */
		<div id="applet-add-new">

			<div className="card mb-4 p-3">

				{/* --+-- HEADER --+-- */}
				<h2 className="mb-3">Add New Task</h2>

				{/* --+-- CLIENT NAME --+-- */}
				<input
					type="text"
					name="title"
					placeholder="Task title"
					className="form-control mb-3"
					value={newTask.title}
					onChange={handleInputChange}
				/>

				{/* --+-- CLIENT DESCRIPTION --+-- */}
				<textarea
					name="description"
					placeholder="Task description"
					className="form-control mb-3"
					value={newTask.description}
					onChange={handleInputChange}
				/>

				{/* --+-- SUBMIT BUTTON --+-- */}
				<button className="btn btn-primary" onClick={addTask}>Add Task</button>

			</div>

		</div>
	);
};
