// import React, { createContext, useContext, useState } from 'react';
// import { SharedStateProvider, SharedStateContext } from './_SharedStateComponent';

import React, { useState } from 'react';

import Navbar from './Navbar';
import Footer from './Footer';

import AppletViewAll from './views/Applet-ViewAll';
import AppletAddNew from './views/Applet-AddNew';

// export const SharedStateContext = createContext();

export function SharedStateProvider() {

  const [appletViewState, setAppletViewState] = useState("home");

  return (
    <>
      <Navbar setAppletViewState = {setAppletViewState} />
      <App appletViewState = {appletViewState} />
    </>
  );

}


export function App({appletViewState}) {  

  // const { appletViewState } = useContext(SharedStateContext);

  return (
    <>

      {appletViewState === "home" ? (
        <>
          <Navbar />
          <div id="applet-window">
            <div className="container-fluid h-100">
              <div className="row h-100">
                <div style="color: 'red';">Hello World!!!</div>
              </div>
            </div>
          </div>
          <Footer />
        </>
      ) : (
        <></>
      )}

      {appletViewState === "clients" ? (
          <SharedStateProvider>
          <Navbar />
          <div id="applet-window">
            <AppletViewAll />
          </div>
          <Footer />
        </SharedStateProvider>
      ) : (
        <></>
      )}

      {appletViewState === "new-client" ? (
          <SharedStateProvider>
          <Navbar />
          <div id="applet-window">
            <AppletAddNew />
          </div>
          <Footer />
        </SharedStateProvider>
      ) : (
        <></>
      )}

    </>
  );
}

export default App;
