import React, { useContext, useState, useEffect } from 'react';
import { SharedStateContext } from './../_SharedStateComponent';


export default function AppletSettings(){

	const { setActiveHue } = useContext(SharedStateContext);

	const setHue = (hue) => {	
		try {

			if (hue === 200) {
				hue = 100;
			} else {
				hue = 200;
			}
			setActiveHue(hue);
		} catch (err) {
			console.log("Error setting the active hue from the settings menu", err);
		}
	}

	return (
		<div id="applet-add-new" className="App container col">

			<div>Hello World</div>
			<button onclick={setHue(setActiveHue)}></button>

			        {/* --+-- ADD NEW CLIENT--+-- */}
					<div className="card mb-4 p-3">
          <h2 className="mb-3">Add New Task</h2>
          <input
            type="text"
            name="title"
            placeholder="Task title"
            className="form-control mb-3"
            value={newTask.title}
            onChange={handleInputChange}
          />
          <textarea
            name="description"
            placeholder="Task description"
            className="form-control mb-3"
            value={newTask.description}
            onChange={handleInputChange}
          />
          <button className="btn btn-primary" onClick={addTask}>Add Task</button>
        </div>

		</div>
	);
};
