import React, { useState } from 'react';

function Sidebar() {

    const [isOpen, setIsOpen] = useState(true);
    const [sidebarClass, setSidebarClass] = useState("col-2");
    const [toggleText, setToggleText] = useState("<<");


    // Toggles the sidebar open or closed.
    const toggleSidebar = () => {
        try {
            if (isOpen === true){
                setIsOpen(false);
                setSidebarClass("sidebar-hidden");
                setToggleText(">>");
            } else {
                setIsOpen(true);
                setSidebarClass("col-2");
                setToggleText("<<");
            };
        } catch (err) {
            console.error('Error toggling the sidebar:', err);
        }
    };

    return(
        
        <>
        
        <div id="sidebar" class={sidebarClass}>

            <div class="container">
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
                <div class="row text-white">Hello World</div>
            </div>

        </div>

        {/* <button id="sidebar-toggle" className="btn btn-primary" onClick={toggleSidebar}>{toggleText}</button> */}
        
        </>

    );

};

export default Sidebar;
