import React, { useContext } from 'react';

import { SharedStateContext } from './_SharedStateComponent';

export default function AppletActiveClientPersonas() {

	const { currentClient } = useContext(SharedStateContext);

	const personas = currentClient.personas;

	if (!personas || personas.length === 0) {
		return <div>No personas available.</div>;
	}

	const personaMap = personas.map(persona => (

        <div key={persona.id} className="view-wrapper persona col-md-6 col-12">
          
		  <h3>{persona.name}</h3>

          <p><strong>Examples:</strong> {persona.examples}</p>
          <p><strong>Summary:</strong> {persona.personaSummary}</p>
          <p><strong>Industry Summary:</strong> {persona.industrySummary}</p>
          <p><strong>Services:</strong> {persona.services}</p>

          {/* Competitors */}
          <div>
			<h3>Competitors:</h3>

            <ul>
              {persona.competitors.map((competitor, index) => (
                <li key={index}>
                  <strong>{competitor.name}</strong>: {competitor.description} <br/>
                  <em>Pros:</em> {competitor.pros} <br/>
                  <em>Cons:</em> {competitor.cons}
                </li>
              ))}
            </ul>
          </div>

          {/* Content Areas */}
          <div>
            <h3>Content Areas:</h3>

            {persona.contentAreas.map((area, index) => (
              <div key={index}>
                <h4>{area.name}</h4>
                <ul>
                  {area.examples.map((example, exIndex) => (
                    <li key={exIndex}>{example}</li>
                  ))}
                </ul>
              </div>
            ))}

          </div>
        </div>

      ))

	return (
		<>
			{/* Window for Target Personas */}
			<div id="tabcontent-personas" className="view-window row tabcontent">

				{/* Header Section */}
				<div className="view-header col-12">
					<h2>Target Personas Overview</h2>
				</div>

				{personaMap()}

			</div>	
		</>
	);
}
