import React, { useContext, useState, useEffect } from 'react';
import { SharedStateContext } from './_SharedStateComponent';
import { ReactComponent as Icon_Sidebar } from "./../assets/layout-sidebar-inset.svg"

import axios from 'axios';

function Sidebar() {

	const { setAppletViewState } = useContext(SharedStateContext);

	const [tasks, setTasks] = useState([]);
	const [isEditing, setIsEditing] = useState(false);
	const [currentTask, setCurrentTask] = useState(null);

	//
	const setView = (view) => {

		try {

			if (view === "clients") {
				document.getElementById("nav-clients").classList.add("active");
				document.getElementById("nav-new-client").classList.remove("active");
				setAppletViewState("clients");

			} else if (view === "new-client") {
				document.getElementById("nav-clients").classList.remove("active");
				document.getElementById("nav-new-client").classList.add("active");
				setAppletViewState("new-client");

			};

		} catch (err) {

			console.error('Error setting applet view from navbar:', err);

		}
	}

	// Fetch tasks on component mount
	useEffect(() => {
		fetchTasks();
	}, []);

	// Fetch all tasks from the back-end API
	const fetchTasks = async () => {
		try {
			const response = await axios.get('http://localhost:3000/tasks');
			setTasks(response.data);
		} catch (err) {
			console.error('Error fetching tasks:', err);
		}
	};

	// Delete a task
	const deleteTask = async (id) => {
		try {
			await axios.delete(`http://localhost:3000/tasks/${id}`);
			setTasks(tasks.filter((task) => task.id !== id));
		} catch (err) {
			console.error('Error deleting task:', err);
		}
	};

	return (
		<>
			{/* SIDEBAR */}
			<nav id="sidebar" className="collapse collapse-horizontal">
				<div id="sidebar-wrapper">

					{/* Sort Selector */}
					{/* <div className="nav-item">
						<div id="sidebar-sort" className="row">
							<button className="btn-sort col">A</button>
							<button className="btn-sort col">B</button>
							<button className="btn-sort col">C</button>
						</div>
					</div> */}

					{/* Display Client List */}
					<div id="nav-clients" className="sidebar-lv1">
						<div className="sidebar-item">
							<div className="sidebar-item-name">Clients</div>
							<button className="sidebar-item-toggle" type="button" data-bs-toggle="collapse" data-bs-target="#client-list" aria-expanded="false" aria-controls="client-list">x</button>
						</div>
						
						<div id="client-list" className="collapse">
							{tasks.map((task) => (
								<div id={task.id} className="sidebar-lv2" onClick={() => setView("clients")} key={task.id}>{task.title}</div>
							))}
						</div>
					</div>
		
					{/* Add New Client */}
					<div id="nav-new-client">
						<div className="sidebar-lv1" onClick={() => setView("new-client")}>New Client</div>
					</div>

				</div>
			</nav>


			{/* SIDEBAR TOGGLE */}
			<button id="sidebar-toggle" type="button" data-bs-toggle="collapse" data-bs-target="#sidebar" aria-expanded="false" aria-controls="sidebar"><Icon_Sidebar /></button>

		</>

	);

};

export default Sidebar;
