import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';

import { SharedStateContext } from './../_SharedStateComponent';

export default function AppletViewActiveClient() {

	const { tasks, setTasks } = useContext(SharedStateContext);
	const { activeClientID } = useContext(SharedStateContext);
	const { activeClient, setActiveClient } = useContext(SharedStateContext);
	
	const [isEditing, setIsEditing] = useState(false);
	const [currentTask, setCurrentTask] = useState(null);

	// Fetch tasks on component mount
	useEffect(() => {
		fetchTasks();
	}, []);

	// Fetch all tasks from the back-end API
	const fetchTasks = async () => {
		try {
			const response = await axios.get(`http://localhost:3000/tasks/${parseInt(activeClientID)}`);
			setActiveClient(response.data);
			console.log(response.data);

		} catch (err) {
			console.error('Error fetching tasks:', err);
		}
	};

	// Delete a task
	const deleteTask = async (id) => {
		try {
			await axios.delete(`http://localhost:3000/tasks/${id}`);
			setTasks(tasks.filter((task) => task.id !== id));
		} catch (err) {
			console.error('Error deleting task:', err);
		}
	};

	// Update a task
	const updateTask = async () => {
		try {
			const response = await axios.put(`http://localhost:3000/tasks/${currentTask.id}`, currentTask);
			setTasks(tasks.map((task) => (task.id === currentTask.id ? response.data : task)));
			setIsEditing(false);
			setCurrentTask(null);
		} catch (err) {
			console.error('Error updating task:', err);
		}
	};

	// Set current task for editing
	const handleEdit = (task) => {
		setCurrentTask(task);
		setIsEditing(true);
	};

	// Handle form input for editing tasks
	const handleEditChange = (e) => {
		const { name, value } = e.target;
		setCurrentTask((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	return (


		<>
			{/* MAIN WINDOW */}
			<div id="applet" className="App container col">

				<div id="applet-view-all" className="App container col">

					{/* Update Existing Task */}
					{isEditing === true ? (
						<div className="card mb-4 p-3">
							<h2 className="mb-3">Edit Task</h2>
							<input
								type="text"
								name="title"
								placeholder="Task title"
								className="form-control mb-3"
								value={currentTask.title}
								onChange={handleEditChange}
							/>
							<textarea
								name="description"
								placeholder="Task description"
								className="form-control mb-3"
								value={currentTask.description}
								onChange={handleEditChange}
							/>
							<button className="btn btn-success" onClick={updateTask}>Update Task</button>
						</div>
					) : (
						<></>
					)}

					{/* Display Tasks */}
					<div className="list-group">
						{activeClient.map((client) => (
							<div className="list-group-item d-flex justify-content-between align-items-center" key={client.id}>
								<div>
									<h5>{client.title}</h5>
									<p>{client.description}</p>
								</div>
								<div>
									<button className="btn btn-danger" onClick={() => deleteTask(client.id)}>Delete</button>
									<button className="btn btn-secondary ms-2" onClick={() => handleEdit(client)}>Edit</button>
								</div>
							</div>
						))}
					</div>

				</div>

			</div>

		</>

	);
};
