import { useState } from "react";

import { appletView } from './Applet';

function Navbar() {

    // const [ appletConst, setAppletConst ] = useState("");

    const setView = (view) => {
        try {
            if (view === "home") {
                document.getElementById("nav-home").classList.add("active");
                document.getElementById("nav-clients").classList.remove("active");
                document.getElementById("nav-new-client").classList.remove("active");
                // setAppletConst("home");
                appletView.value = "home";
            } else if (view === "clients") {
                document.getElementById("nav-home").classList.remove("active");
                document.getElementById("nav-clients").classList.add("active");
                document.getElementById("nav-new-client").classList.remove("active");
                // setAppletConst("clients");
                appletView.value = "clients";
            } else if (view === "new-client") {
                document.getElementById("nav-home").classList.remove("active");
                document.getElementById("nav-clients").classList.remove("active");
                document.getElementById("nav-new-client").classList.add("active");
                // setAppletConst("new-client");
                appletView.value = "new-client";
            };
            console.log("[NAVBAR] Current View: " + view);
            
        } catch (err) {
            console.error('Error setting applet view from navbar:', err);
        }
    }

    // appletView.value = appletConst;

    console.log("[NAVBAR] Applet View = " + appletView.value);

    return(
        
        /* NAVBAR */
        <nav id="navbar" className="navbar sticky-top navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">

                <div className="collapse navbar-collapse" id="navbarNav">

                    <ul className="navbar-nav">
                        <li className="nav-item" onClick={() => setView("home")}>
                            <a id="nav-home" className="nav-link active">Home</a>
                        </li>
                        <li className="nav-item" onClick={() => setView("clients")}>
                            <a id="nav-clients" className="nav-link">Clients</a>
                        </li>
                        <li className="nav-item" onClick={() => setView("new-client")}>
                            <a id="nav-new-client" className="nav-link">[Add New Client]</a>
                        </li>
                    </ul>

                </div>
                
            </div>
        </nav>
    );

};

export default Navbar;
