import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';

import { SharedStateContext } from './_SharedStateComponent';

import AppletActiveClientAbout from './Applet-ActiveClient-About';
import AppletActiveClientPersonas from './Applet-ActiveClient-Personas';
import AppletActiveClientTopics from './Applet-ActiveClient-Topics';
import AppletActiveClientSettings from './Applet-ActiveClient-Settings';

export default function AppletViewActiveClient() {

	const { setAppletViewState, clients, setClients, activeClientID, setActiveClientID, currentClient, setCurrentClient } = useContext(SharedStateContext);

	const [openTab, setOpenTab] = useState("about");

    useEffect(() => {
		setClient();
		setOpenTab("about");
    }, [activeClientID]);

	//------------------------------------------------------------------------------------
	//-- TODO: Update this so that the edit only updates an individual field -------------
	//------------------------------------------------------------------------------------

	const [isEditing, setIsEditing] = useState(false);

	// Handle form input for editing clients
	const handleEditChange = (e) => {
		const { name, value } = e.target;
		setCurrentClient((prev) => ({
			...prev,
			[name]: value,
		}));
	};

	// Update a client
	const updateClient = async () => {
		try {
			await axios.put(`http://localhost:3000/clients/${currentClient.id}`, currentClient)
			setClients(clients.map((client) => (client.id === currentClient.id ? currentClient : client)));
			setActiveClientID(currentClient.id);
			setAppletViewState("active-clients");
			setOpenTab('about');
		} catch (err) {
			console.error('Error updating client:', err);
			alert('Failed to update client. Please try again.');
		}
	};

	// Delete a client
	const deleteClient = async (id) => {
		try {
			await axios.delete(`http://localhost:3000/clients/${id}`);
			setClients(clients.filter((client) => client.id !== id));
			setActiveClientID("all-clients");
			setAppletViewState("all-clients");
		} catch (err) {
			console.error('Error deleting client:', err);
			alert('Failed to delete client. Please try again.');
		}
	};

	var clientEdits = <></>;

	if (isEditing === true) {
		clientEdits =  
		<>

			{/* Header */}
			<h4 className="mb-3">Edit Client</h4>

			{/* Client Name */}
			<h6>Name:</h6>
			<input
				type="text"
				name="name"
				placeholder="Client Name"
				className="form-control mb-3"
				value={currentClient.name}
				onChange={handleEditChange}
			/>

			{/* Client Description */}
			<h6>Description:</h6>
			<textarea
				name="description"
				placeholder="Client Description"
				className="form-control mb-3"
				value={currentClient.description}
				onChange={handleEditChange}
			/>

			{/* Client Description Add-on */}
			<h6>Description &#40;Extended&#41;:</h6>
			<textarea
				name="descriptionaddon"
				placeholder="Additional Client Information"
				className="form-control mb-3"
				value={currentClient.descriptionaddon}
				onChange={handleEditChange}
			/>

			{/* Client Website */}
			<h6>Website:</h6>
			<input
				type="text"
				name="website"
				placeholder="Client Website"
				className="form-control mb-3"
				value={currentClient.website}
				onChange={handleEditChange}
			/>

			{/* Client LinkedIn */}
			<h6>LinkedIn:</h6>
			<input
				type="text"
				name="linkedin"
				placeholder="Client LinkedIn"
				className="form-control mb-3"
				value={currentClient.linkedin}
				onChange={handleEditChange}
			/>

			{/* Client YouTube */}
			<h6>YouTube:</h6>
			<input
				type="text"
				name="youtube"
				placeholder="Client YouTube"
				className="form-control mb-3"
				value={currentClient.youtube}
				onChange={handleEditChange}
			/>

			{/* Client Twitter */}
			<h6>X &#40;Twitter&#41;:</h6>
			<input
				type="text"
				name="twitter"
				placeholder="Client X (Twitter)"
				className="form-control mb-3"
				value={currentClient.twitter}
				onChange={handleEditChange}
			/>

			{/* Client Facebook */}
			<h6>Facebook:</h6>
			<input
				type="text"
				name="facebook"
				placeholder="Client Facebook"
				className="form-control mb-3"
				value={currentClient.facebook}
				onChange={handleEditChange}
			/>

			{/* Client Instagram */}
			<h6>Instagram:</h6>
			<input
				type="text"
				name="instagram"
				placeholder="Client Instagram"
				className="form-control mb-3"
				value={currentClient.instagram}
				onChange={handleEditChange}
			/>

			{/* Client TikTok */}
			<h6>TikTok:</h6>
			<input
				type="text"
				name="tiktok"
				placeholder="Client TikTok"
				className="form-control mb-3"
				value={currentClient.tiktok}
				onChange={handleEditChange}
			/>

			{/* Client Products & Services */}
			<h6>Products & Services:</h6>
			<input
				type="text"
				name="products"
				placeholder="Client Products & Services"
				className="form-control mb-3"
				value={currentClient.products}
				onChange={handleEditChange}
			/>

			{/* Client Target Personas */}
			<h6>Target Personas:</h6>
			<textarea
				name="personas"
				placeholder="Client Target Persona"
				className="form-control mb-3"
				value={currentClient.personas}
				onChange={handleEditChange}
			/>

			{/* Submit Button */}
			<button className="btn btn-success" onClick={updateClient}>Update Client</button>

		</>
	} else {
		<></>
	};

	//------------------------------------------------------------------------------------
	//------------------------------------------------------------------------------------

	const setClient = async () => {
		const response = await axios.get(`http://localhost:3000/clients/${activeClientID}`);
		setCurrentClient(response.data);
	};

	const renderTabView = () => {

		switch (openTab) {
			case 'about': return <AppletActiveClientAbout />;
			case 'personas': return <AppletActiveClientPersonas />;
			case 'topics': return <AppletActiveClientTopics />;
			case 'settings': return <AppletActiveClientSettings />;
			default: return null;
		}
	};

	return (

		/* --+--+-- VIEW ACTIVE CLIENT --+--+-- */
		<div id="applet-view-active" className="applet-view">

			{/* Mini Nav Bar */}
			<div className="container view-container">

				{/* Tabs for navigating client sub-views */}
				<div className="row view-tabs">

					<div className="tabs-title"><h1>{currentClient.name}</h1></div>

					<div className="tabs-buttons">
						<button id="tabbutton-about" className={`tabbutton ${openTab === 'about' ? 'active' : ''}`} onClick={() => setOpenTab('about')}>About</button>
						<button id="tabbutton-personas" className={`tabbutton ${openTab === 'personas' ? 'active' : ''}`} onClick={() => setOpenTab('personas')}>Target Personas</button>
						<button id="tabbutton-topics" className={`tabbutton ${openTab === 'topics' ? 'active' : ''}`} onClick={() => setOpenTab('topics')}>Topics</button>
						<button id="tabbutton-settings" className={`tabbutton ${openTab === 'settings' ? 'active' : ''}`} onClick={() => setOpenTab('settings')}>Settings</button>
					</div>

				</div>

				{renderTabView()}


			</div>
		</div>
	);
};
