import React, { useContext, useState, useEffect } from 'react';
import { SharedStateContext } from './_SharedStateComponent';
import { ReactComponent as Icon_Sidebar } from "./../assets/layout-sidebar-inset.svg"
import { ReactComponent as Caret } from "./../assets/caret-down-fill.svg"

import axios from 'axios';

function Sidebar() {

	const { setAppletViewState } = useContext(SharedStateContext);

	const [tasks, setTasks] = useState([]);

	// Updates the applet view based on selection
	const setView = (view) => {

		try {
			if (view === "clients") {
				document.getElementById("nav-clients").classList.add("active");
				document.getElementById("nav-new-client").classList.remove("active");
				setAppletViewState("clients");
			} else if (view === "new-client") {
				document.getElementById("nav-clients").classList.remove("active");
				document.getElementById("nav-new-client").classList.add("active");
				setAppletViewState("new-client");
			};
		} catch (err) {
			console.error('Error setting applet view from navbar:', err);
		}
	}

	// Fetch tasks on component mount
	useEffect(() => {
		fetchTasks();
	}, []);

	// Fetch all tasks from the back-end API
	const fetchTasks = async () => {
		try {
			const response = await axios.get('http://localhost:3000/tasks');
			setTasks(response.data);
		} catch (err) {
			console.error('Error fetching tasks:', err);
		}
	};

	return (
		<>
			{/* SIDEBAR */}
			<nav id="sidebar" className="collapse collapse-horizontal">
				<div id="sidebar-wrapper">

					{/* Display Client List */}
					<div id="nav-clients" className="sidebar-section">
						
						<div className="sidebar-item" data-bs-toggle="collapse" data-bs-target="#client-list" aria-expanded="false" aria-controls="client-list">
							<div className="sidebar-item-name">Clients</div>
							{/* <button className="sidebar-item-toggle" type="button" data-bs-toggle="collapse" data-bs-target="#client-list" aria-expanded="false" aria-controls="client-list"><Caret /></button> */}
							<div className="sidebar-item-toggle"><Caret /></div>
						</div>
						
						<div id="client-list" className="sidebar-item collapse">
							{tasks.map((task) => (
								<div id={task.id} className="sidebar-subitem" key={task.id} onClick={() => setView("clients")}>
									<div className="sidebar-item-name">{task.title}</div>
								</div>
							))}
						</div>
					</div>

					{/* Sample Sidebar Section */}
					<div id="nav-clients" className="sidebar-section">
						
						<div className="sidebar-item" onClick={() => setView("")}>
							<div className="sidebar-item-name">Sample</div>
						</div>

					</div>
		
					{/* Add New Client */}
					<div id="nav-new-client" className="sidebar-section">
						<div className="sidebar-item" onClick={() => setView("new-client")}>
							<div className="sidebar-item-name">New Client</div>
						</div>
					</div>

				</div>
			</nav>


			{/* SIDEBAR TOGGLE */}
			<button id="sidebar-toggle" type="button" data-bs-toggle="collapse" data-bs-target="#sidebar" aria-expanded="false" aria-controls="sidebar"><Icon_Sidebar /></button>

		</>

	);

};

export default Sidebar;
