import React, { useState } from 'react';
import axios from 'axios';

import Applet from './Applet';

function AppletAddNew() {
  const [newTask, setNewTask] = useState({ title: '', description: '' });

  // Handle form input for new tasks
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Create a new task
  const addTask = async () => {
    try {
      const response = await axios.post('http://localhost:3000/tasks', newTask);
      Applet.setTasks([...Applet.tasks, response.data]);
      setNewTask({ title: '', description: '' });
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  return (
    <div id="applet-add-new" className="App container col">

        {/* --+-- ADD NEW CLIENT--+-- */}
        <div className="card mb-4 p-3">
          <h2 className="mb-3">Add New Task</h2>
          <input
            type="text"
            name="title"
            placeholder="Task title"
            className="form-control mb-3"
            value={newTask.title}
            onChange={handleInputChange}
          />
          <textarea
            name="description"
            placeholder="Task description"
            className="form-control mb-3"
            value={newTask.description}
            onChange={handleInputChange}
          />
          <button className="btn btn-primary" onClick={addTask}>Add Task</button>
        </div>

    </div>
  );
}

export default AppletAddNew;
